        //     const imageUrl = URL.createObjectURL(blob);
        //     context.commit("setQrCodePictureArr",{imageUrl,height});
        //     }
        // })
        }
       }
    },
    state:{
       QRCodeIdArr:[],
       QrCodePictureArr:[]
    }
}
