import Vue from "vue";
import Vuex from "vuex";
import axios from "axios";
const mutations ={
    getCabinetArr(state,param){
        state.CabinetArr = param.CabinetArr;
        if(param.pageNum!==0){
            state.pageNum = param.pageNum;
        }
        state.cabinetId = param.cabinetId;
    },
    getNavigation(state,navigation){
        state.navigation = navigation;
    },
    setBoxid(state,param){
      let boxId = param.boxId;
      let positionArr = param.positionArr;
        if(boxId!==""){
            state.CabinetArr.at(parseInt(positionArr[0])).at(parseInt(positionArr[1])).dataId = boxId;
        }
    },
    setPageSelect(state,pageSelect){
        state.pageSelect = pageSelect;
    },
    setWarningMsg(state,param){
        if(state.warningMsg==="上架成功！"){
            state.warningMsg = param.message;
            state.typeMsg = param.type;
            setTimeout(()=>{
                window.parent.win.doOkFunctionAndClose();
                window.parent.top.WindowExitAction1.doAction();
            });
        }
        else{
            state.warningMsg = param.message;
            state.typeMsg = param.type;
        }
    }

};
const  actions = {
    //获取 显示列表数据
    getCabinetArr(context,param){
        let cabinetIds = param.cabinetId==""?context.state.cabinetId:param.cabinetId;
        context.commit("setPageSelect",param.pageSelect);
      axios.post("/OSPApplication/cabinet/loadCabinet",{"cabinetId":cabinetIds,"face":param.pageSelect}).then(
                Response=>{
                    if(Response.data.code==="0"){
                        context.commit("getCabinetArr",{
                            "CabinetArr":Response.data.data,"pageNum":param.pageNum,"cabinetId":cabinetIds
                          });
                        } else {
                            context.commit("setWarningMsg",{"message":Response.data.message,"type":"error"})
                        }
                },
                error=>{
                   
                });
    },
    //点击空格设置盒号
    setBoxid(context,param){
        if(param.boxId===null||param.boxId===""){
            context.commit("setWarningMsg",{"message":"没有可以上架的盒，请再次选择需要上架的盒！","type":"error"});
            return;
        }
        debugger
        axios.post("/OSPApplication/cabinet/upCabinet",{"boxId":param.boxId,"id":param.fileBag}).then(
            Response=>{
                if(Response.data.code==="0"){
                    localStorage.removeItem("BoxingId");
                    context.commit("setBoxid",{"boxId":param.boxId,"positionArr":param.positionArr});
                    context.commit("setWarningMsg",{"message":"上架成功！","type":"success"})
                }else{
                    context.commit("setWarningMsg",{"message":Response.data.message,"type":"error"})
                }
            },
            error=>{
                
            });
    },
    //获取菜单
    getNavigation(context){
        axios.post("/OSPApplication/cabinet/loadRepository").then(
            Response=>{
                if(Response.data.code==="0"){
                    context.commit("getNavigation",Response.data.data);
                }else{
                    context.commit("setWarningMsg",{"message":Response.data.message,"type":"error"})
                }
            },
            error=>{
                
            });
    },
    //修改面
    setPageSelect(context,pageSelect){
        context.commit("setPageSelect",pageSelect);
        let cabinetIds = context.state.cabinetId;
        axios.post("/OSPApplication/cabinet/loadCabinet",{"cabinetId":cabinetIds,"face":pageSelect}).then(
            Response=>{
                if(Response.data.code==="0"){
                    context.commit("getCabinetArr",{
                        "CabinetArr":Response.data.data,"pageNum":0,"cabinetId":cabinetIds
                    });
                }else{
                    context.commit("setWarningMsg",{"message":Response.data.message,"type":"error"})
                }
            },
            error=>{
            
