import axios from "axios"
export default {
    namespaced:true,
    mutations:{
        getBorrowVoucher(state,data){
            state.borrowApplicationArr = data.borrowApplicationDTOList;
            state.dueVoucherList = data.dueVoucherDTOList;
            state.borrowComponentTotal = data.borrowTotal.borrowComponentTotal;
            state.borrowVoucherTotal = data.borrowTotal.borrowVoucherTotal;
            if(data.dueVoucherDTOList && data.dueVoucherDTOList.length>0){
                state.dueVoucherSum =  data.dueVoucherDTOList[0].dueVoucherSum;
            } else{
                state.dueVoucherSum = 0 ;
            }
        },
        getInformation(state,data){
            state.informationArr = data;
        }
    },
    actions:{
        getBorrowVoucher(context){
            axios.post(`/OSPApplication/firstPage/borrowVoucher/${context.state.userName||window.parent.parent.reactGPO.UserName}`).then(Response=>{
                if(Response.data.code==="0"){
                    context.commit("getBorrowVoucher",Response.data.data);
                } else{
                    context.commit(
                        "grounding/setWarningMsg",
                        { message: Response.data.message, type: "error" },
                        { root: true }
                      );
                }
            },
            error=>{
                context.commit(
                    "grounding/setWarningMsg",
                    { message: error.data, type: "error" },
                    { root: true }
                  );
            })
        },
        getInformation(context){
            axios.post(`/OSPApplication/firstPage/getInformation`).then(Response=>{
                if(Response.data.code==="0"){
                    context.commit("getInformation",Response.data.data);
                } else{
                    context.commit(
                        "grounding/setWarningMsg",
                        { message: Response.data.message, type: "error" },
                        { root: true }
                      );
                }
            },
            error=>{
                context.commit(
                    "grounding/setWarningMsg",
                    { message: error.data, type: "error" },
                    { root: true }
                  );
            })
        }
    },
    state:{
        userHeaderCircleUrl:require("@/static/userHeader.png"),
        userName:"9999",
        userCaption:"管理员",
        userJob:"华发物业",
        dueVoucherSum:0,
        dueVoucherList:[],
        borrowVoucherTotal:0,
        borrowComponentTotal:0,
        borrowApplicationArr:[],
        archivesPolicyArr:[{"orgName":"集团","policyMessage":"集团档案管理规章制度"},{"orgName":"人事","policyMessage":"2023年新员工手册内容更新"},{"orgName":"系统","policyMessage":"2022年4月VI-财务中台升级通"},{"orgName":"政策","policyMessage":"员工2022年全年个税-专项附加税扣除"}],
        informationArr:[{yearAndMount:"2022-08",day:"30",informationTiTle:"电子档案系统升级",informationDetails:"于2023年8月20日下午1:30升级，届时服务器"},{yearAndMount:"2022-08",day:"20",informationTilte:"电子档案APP上线啦",informationDetails:"电子档案APP经过几个月的努力，终于上线"},{yearAndMount:"2022-08",day:"15",informationTilte:"关于系统培训通知",informationDetails:"电子档案系统使用培训"}]
    }
}
