import axios from "axios";
import $ from "jquery";
export default {
    namespaced:true,
    mutations:{
        setQRCodeIdArr(state,idArr){
            state.QRCodeIdArr = idArr;
        },
        setQrCodePictureArr(state,picture){
            state.QrCodePictureArr.push(picture);
        },
        clearQrCodePictureArr(state){
            while(state.QrCodePictureArr.pop()){}
        },
        clearQRCodeIdArr(state){
            while(state.setQRCodeIdArr.pop()){}
        }
    },
    actions:{
       getQRCodePictureArr(context,vc){
        context.commit("clearQrCodePictureArr");
        if(context.state.QRCodeIdArr===null || context.state.QRCodeIdArr === "" || context.state.QRCodeIdArr.length===0){
            context.commit("grounding/setWarningMsg",{"message":"请选择一条数据后再点击生成二维码！","type":"error"},{root:true});
            return;
        }
        var codeIdSum = 0;
        var codeIdList = [];
        context.state.QRCodeIdArr.forEach(codeIdMap => {
            codeIdList.push(codeIdMap.cabinetName);
            codeIdSum++;
            if(codeIdSum%4===0){
                getAsyncAxios(`/OSPApplication/general/generateQRCode/${codeIdList.join(",")}`,"1000px");
                codeIdList = [];
                codeIdSum = 0;
            }
        });
        if(codeIdList.length>0){
            getAsyncAxios(`/OSPApplication/general/generateQRCode/${codeIdList.join(",")}`,`${(codeIdList.length*250)}px`);
        }
        if(vc.$route.name !=="ImgPicture"){
            vc.$router.push({"name":"ImgPicture"});
            context.commit("clearQRCodeIdArr");
          }
